import React, { useState } from "react";
import "./Quicksort.css";

function Quicksort() {
  const [arr, setArr] = useState([]);
  const [inputArr, setInputArr] = useState("");
  const [isSorting, setIsSorting] = useState(false);
  const [steps, setSteps] = useState([]);
  const [pivotPosition, setPivotPosition] = useState(null);
  const [initialPivotValue, setInitialPivotValue] = useState(null);
  const handleInputChange = (e) => {
    setInputArr(e.target.value);
  };

  const handleGenerateRandomArray = () => {
    const randomArr = generateRandomArray(15);
    setArr(randomArr);
    setSteps([]);
    setPivotPosition(null);
  };

  const handleSortArray = async () => {
    setIsSorting(true);
    const arrToSort = [...arr];
    const sortingSteps = [];

    // Select the initial pivot
    const pivotIndex = arrToSort.length - 1;
    setPivotPosition(pivotIndex); // Update to set the pivot index directly
    setInitialPivotValue(arr[pivotIndex]); // Set the initial pivot value

    sortingSteps.push([
      arrToSort.map((_, index) => arrToSort[index]),
      pivotIndex,
      arrToSort[pivotIndex],
      true,
    ]);
    setSteps([...sortingSteps]);

    await quicksort(arrToSort, 0, arrToSort.length - 1, sortingSteps);
    setIsSorting(false);
  };

  const generateRandomArray = (length) => {
    const arr = [];
    for (let i = 0; i < length; i++) {
      arr.push(Math.floor(Math.random() * 100));
    }
    return arr;
  };

  const highlightPivot = async (arrToSort, sortingSteps) => {
    let pivotHighlighted = false;

    while (!pivotHighlighted) {
      // Find the current index of the pivot value in the original array
      const pivotIndex = arr.indexOf(initialPivotValue);
      if (pivotIndex === -1) {
        // Pivot not found, break the loop
        break;
      }

      // Find the corresponding index of the pivot value in the sorted array
      const sortedPivotIndex = arrToSort.indexOf(arrToSort[pivotPosition]);

      // Highlight the pivot value at its current index in the sorted array
      sortingSteps.push([
        arrToSort.map((_, index) => arrToSort[index]),
        sortedPivotIndex,
        arrToSort[pivotPosition],
        true,
      ]);
      setSteps([...sortingSteps]);

      await new Promise((resolve) => setTimeout(resolve, 300)); // Adjust the delay as needed

      // Check if the pivot has moved to a different position
      if (pivotIndex !== sortedPivotIndex) {
        pivotHighlighted = false;
      } else {
        pivotHighlighted = true;
      }
    }
  };

  const quicksort = async (arr, start, end, steps) => {
    if (start >= end) {
      return;
    }

    const pivotIndex = await partition(arr, start, end, steps);
    await Promise.all([
      quicksort(arr, start, pivotIndex - 1, steps),
      quicksort(arr, pivotIndex + 1, end, steps),
    ]);
  };

  const partition = async (arr, start, end, steps) => {
  const pivot = arr[end];
  let i = start - 1;

  for (let j = start; j < end; j++) {
    if (arr[j] < pivot) {
      i++;
      await swap(arr, i, j, steps);
    }
  }

  await swap(arr, i + 1, end, steps);

  setPivotPosition(i + 1); // Update pivot position

  // Call highlightPivot after partitioning step
  await highlightPivot(arr, steps);

  return i + 1;
};


  const swap = async (arr, i, j, steps) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
        setArr([...arr]);

        // Highlight the pivot value for each iteration
        steps.push([arr.map((_, index) => arr[index]), i === pivotPosition || j === pivotPosition, pivotPosition]);
        setSteps([...steps]);

        resolve();
      }, 300); // Adjust the delay as needed
    });
  };



  return (
    <div className="quicksort-container">
      <div className="options">
        <input
          type="text"
          placeholder="Enter array (e.g., 1, 2, 3)"
          value={inputArr}
          onChange={handleInputChange}
        />
        <button onClick={() => setArr(inputArr.split(",").map(Number))}>
          Use Input Array
        </button>
        <button onClick={handleGenerateRandomArray}>
          Generate Random Array
        </button>
        <button onClick={handleSortArray} disabled={isSorting}>
          Sort Array
        </button>
      </div>
      <div className="array-container">
        <div className="step">
          {arr.map((num, index) => (
            <div
              key={index}
              className={`circle ${
                steps.some(([_, j, isPivot]) => j === index && isPivot)
                  ? "pivot-highlight"
                  : ""
              }`}
            >
              {num}
            </div>
          ))}
        </div>
        <div className="pivot-value">
       Pivot Value: {arr[pivotIndex]}
     </div>
      </div>
    </div>
  );
}

export default Quicksort;
