import React, { useState } from 'react';
import './Quicksort.css';

function Quicksort() {
  const [arr, setArr] = useState([]);
  const [inputArr, setInputArr] = useState('');
  const [isSorting, setIsSorting] = useState(false);
  const [steps, setSteps] = useState([]);

  const handleInputChange = (e) => {
    setInputArr(e.target.value);
  };

  const handleGenerateRandomArray = () => {
    const randomArr = generateRandomArray(10);
    setArr(randomArr);
    setSteps([]);
  };

  const handleSortArray = () => {
    setIsSorting(true);
    const arrToSort = [...arr];
    const sortingSteps = [];
    quicksort(arrToSort, 0, arrToSort.length - 1, sortingSteps);
    setSteps(sortingSteps);
    setIsSorting(false);
  };

  const generateRandomArray = (length) => {
    const arr = [];
    for (let i = 0; i < length; i++) {
      arr.push(Math.floor(Math.random() * 100));
    }
    return arr;
  };

  const quicksort = (arr, start, end, steps) => {
    if (start >= end) {
      return;
    }

    const pivotIndex = partition(arr, start, end, steps);

    quicksort(arr, start, pivotIndex - 1, steps);
    quicksort(arr, pivotIndex + 1, end, steps);
  };

  const partition = (arr, start, end, steps) => {
    const pivot = arr[end];
    let i = start - 1;

    for (let j = start; j < end; j++) {
      if (arr[j] < pivot) {
        i++;
        swap(arr, i, j);
        steps.push([...arr]);
      }
    }

    swap(arr, i + 1, end);
    steps.push([...arr]);

    return i + 1;
  };

  const swap = (arr, i, j) => {
    const temp = arr[i];
    arr[i] = arr[j];
    arr[j] = temp;
  };

  return (
    <div className="quicksort-container">
      <div className="options">
        <input
          type="text"
          placeholder="Enter array (e.g., 1, 2, 3)"
          value={inputArr}
          onChange={handleInputChange}
        />
        <button onClick={() => setArr(inputArr.split(',').map(Number))}>
          Use Input Array
        </button>
        <button onClick={handleGenerateRandomArray}>Generate Random Array</button>
        <button onClick={handleSortArray} disabled={isSorting}>
          Sort Array
        </button>
      </div>
      <div className="array-container">
        {steps.map((step, index) => (
          <div key={index} className="step">
            {step.map((num, index) => (
              <div
                key={index}
                className={`circle ${num === step[step.length - 1] ? 'pivot' : ''} ${
                  isSorting && num === step[index - 1] ? 'highlight' : ''
                }`}
              >
                {num}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Quicksort;
