import React, { useState } from 'react';
import './Quicksort.css';

function Quicksort() {
  const [arr, setArr] = useState([]);
  const [inputArr, setInputArr] = useState('');
  const [isSorting, setIsSorting] = useState(false);
  const [steps, setSteps] = useState([]);
  const [pivotIndex, setPivotIndex] = useState(null);

  const handleInputChange = (e) => {
    setInputArr(e.target.value);
  };

  const handleGenerateRandomArray = () => {
    const randomArr = generateRandomArray(10);
    setArr(randomArr);
    setSteps([]);
    setPivotIndex(null);
  };

  const handleSortArray = async () => {
    setIsSorting(true);
    const arrToSort = [...arr];
    const sortingSteps = [];

    await quicksort(arrToSort, 0, arrToSort.length - 1, sortingSteps);
    setIsSorting(false);
  };

  const generateRandomArray = (length) => {
    const arr = [];
    for (let i = 0; i < length; i++) {
      arr.push(Math.floor(Math.random() * 100));
    }
    return arr;
  };

  const quicksort = async (arr, start, end, steps) => {
    if (start >= end) {
      return;
    }

    const pivotIndex = await partition(arr, start, end, steps);

    await Promise.all([
      quicksort(arr, start, pivotIndex - 1, steps),
      quicksort(arr, pivotIndex + 1, end, steps),
    ]);
  };

  const partition = async (arr, start, end, steps) => {
    const pivot = arr[end];
    let i = start - 1;

    for (let j = start; j < end; j++) {
      if (arr[j] < pivot) {
        i++;
        await swap(arr, i, j, steps);
      }
    }

    await swap(arr, i + 1, end, steps);

    return i + 1;
  };

  const swap = async (arr, i, j, steps) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
        setArr([...arr]);

        // Highlight the pivot value for each iteration
        if (pivotIndex !== null) {
          steps.push([pivotIndex, pivotIndex, false]);
          setSteps([...steps]);
        }

        resolve();
      }, 300); // Adjust the delay as needed
    });
  };

  return (
    <div className="quicksort-container">
      <div className="options">
        <div className="input-container">
          <input
            type="text"
            placeholder="Enter array (e.g., 1, 2, 3)"
            value={inputArr}
            onChange={handleInputChange}
            className="input-field"
          />
          <button
            onClick={() => setArr(inputArr.split(',').map(Number))}
            className="button"
          >
            Use Input Array
          </button>
        </div>
        <button
          onClick={handleGenerateRandomArray}
          className="button"
        >
          Generate Random Array
        </button>
        <button
          onClick={handleSortArray}
          disabled={isSorting}
          className="button"
        >
          Sort Array
        </button>
      </div>
      <div className="array-container">
        {arr.map((num, index) => (
          <div
            key={index}
            className={`circle ${
              steps.some(([i, j, isPivot]) => i === index && isPivot)
                ? 'pivot-highlight'
                : ''
            }`}
          >
            {num}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Quicksort;
