import React, { useState } from "react";
import "./Quicksort.css";

function Quicksort() {
  const [arr, setArr] = useState([]);
  const [inputArr, setInputArr] = useState("");
  const [isSorting, setIsSorting] = useState(false);
  const [steps, setSteps] = useState([]);
  const [pivotPosition, setPivotPosition] = useState(null);
  const [highlightedIndices, setHighlightedIndices] = useState([]);

  const handleInputChange = (e) => {
    setInputArr(e.target.value);
  };

  const handleGenerateRandomArray = () => {
    const randomArr = generateRandomArray(10);
    setArr(randomArr);
    setSteps([]);
    setPivotPosition(null);
    setHighlightedIndices([]);
  };

  const handleSortArray = async () => {
    setIsSorting(true);
    const arrToSort = [...arr];
    const sortingSteps = [];

    // Select the initial pivot
    const pivotIndex = arrToSort.length - 1;
    const pivotValue = arrToSort[pivotIndex];
    setPivotPosition(pivotValue);

    sortingSteps.push([
      arrToSort.map((_, index) => arrToSort[index]),
      pivotIndex,
      pivotValue,
      true,
    ]);
    setSteps([...sortingSteps]);

    await quicksort(arrToSort, 0, arrToSort.length - 1, sortingSteps);
    setIsSorting(false);
  };

  const generateRandomArray = (length) => {
    const arr = [];
    for (let i = 0; i < length; i++) {
      arr.push(Math.floor(Math.random() * 100));
    }
    return arr;
  };

  const highlightPivot = async (arrToSort, sortingSteps) => {
    while (true) {
      // Find the current index of the pivot value
      const pivotIndex = arrToSort.indexOf(pivotPosition);
      if (pivotIndex === -1) {
        // Pivot not found, break the loop
        break;
      }

      // Update the highlightedIndices state with the pivotIndex
      setHighlightedIndices([pivotIndex]);

      await new Promise((resolve) => setTimeout(resolve, 300)); // Adjust the delay as needed
    }
  };

  const quicksort = async (arr, start, end, steps) => {
    if (start >= end) {
      return;
    }

    const pivotIndex = await partition(arr, start, end, steps);
    await Promise.all([
      quicksort(arr, start, pivotIndex - 1, steps),
      quicksort(arr, pivotIndex + 1, end, steps),
    ]);

    // Start highlighting the pivot value as it moves
    await highlightPivot(arr, steps);
  };

  const partition = async (arr, start, end, steps) => {
    const pivot = arr[end];
    let i = start - 1;

    for (let j = start; j < end; j++) {
      if (arr[j] < pivot) {
        i++;
        await swap(arr, i, j, steps, arr);
      }
    }

    await swap(arr, i + 1, end, steps, arr);

    return i + 1;
  };

  const swap = async (arr, i, j, steps, arrToSort) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
        setArr([...arr]);

        // Highlight the pivot value for each iteration
        const pivotIndex = arr.indexOf(arrToSort[pivotPosition]);
        if (pivotIndex !== -1) {
          steps.push([
            arrToSort.map((_, index) => arrToSort[index]),
            pivotIndex,
            pivotPosition,
            true,
          ]);
        }

        resolve();
      }, 300); // Adjust the delay as needed
    });
  };

  return (
    <div className="quicksort-container">
      <div className="options">
        <input
          type="text"
          placeholder="Enter array (e.g., 1, 2, 3)"
          value={inputArr}
          onChange={handleInputChange}
        />
        <button onClick={() => setArr(inputArr.split(",").map(Number))}>
          Use Input Array
        </button>
        <button onClick={handleGenerateRandomArray}>
          Generate Random Array
        </button>
        <button onClick={handleSortArray} disabled={isSorting}>
          Sort Array
        </button>
      </div>
      <div className="array-container">
        {steps.map((step, stepIndex) => (
          <div key={stepIndex} className="step">
            {step.map((num, index) => (
              <div
                key={index}
                className={`index-circle ${
                  highlightedIndices.includes(num[1]) ? "pivot-highlight" : ""
                }`}
              >
                {num[0]}
              </div>
            ))}
          </div>
        ))}
        <div className="step">
          {arr.map((num, index) => (
            <div
              key={index}
              className={`circle ${
                highlightedIndices.includes(index) ? "pivot-highlight" : ""
              }`}
            >
              {num}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Quicksort;
