import React, { useState } from "react";
import "./Quicksort.css";

// Add this function definition before the component definition
const isSorted = (array, index) => {
  return index < array.length - 1 && array[index] <= array[index + 1];
};

const QuickSortVisualizer = () => {
  const [array, setArray] = useState(generateRandomArray(15));
  const [pivot, setPivot] = useState(null);
  const [comparing, setComparing] = useState([]);
  const [isSorting, setIsSorting] = useState(false);

  const generateRandomArray = (size) => {
    const array = [];
    for (let i = 0; i < size; i++) {
      array.push(Math.floor(Math.random() * 101));
    }
    return array;
  };

  const quickSort = (array, start, end) => {
    if (start >= end) {
      return;
    }

    setPivot(array[end]);
    const pivotIndex = partition(array, start, end);

    quickSort(array, start, pivotIndex - 1);
    quickSort(array, pivotIndex + 1, end);
  };

  const partition = (array, start, end) => {
    const pivot = array[end];
    let pivotIndex = start;

    for (let i = start; i < end; i++) {
      if (array[i] <= pivot) {
        setComparing([array[i], pivot]);
        [array[i], array[pivotIndex]] = [array[pivotIndex], array[i]];
        pivotIndex++;
      }
    }

    [array[pivotIndex], array[end]] = [array[end], array[pivotIndex]];

    return pivotIndex;
  };

  const handleSort = () => {
    setIsSorting(true);
    const newArray = [...array];
    quickSort(newArray, 0, newArray.length - 1);
    setArray(newArray);
    setIsSorting(false);
  };

  const handlePause = () => {
    setIsSorting(false);
  };

  const handleResume = () => {
    setIsSorting(true);
    handleSort();
  };

  return (
    <div>
      <button onClick={handleSort}>Sort</button>
      <button onClick={handlePause}>Pause</button>
      <button onClick={handleResume}>Resume</button>
      <div>
        {array.map((num, index) => (
          <div
            key={index}
            className={`number ${index === pivot ? "pivot" : ""} ${
              isSorted(array, index) ? "sorted" : ""
            } ${comparing.includes(num) ? "comparing" : ""}`}
            onClick={() => setPivot(index)}
          >
            {num}
          </div>
        ))}
      </div>
    </div>
  );
};

export default QuickSortVisualizer;
