import React, { useState } from 'react';

function App() {
 const [array, setArray] = useState([]);
 const [randomArray, setRandomArray] = useState([]);
 const [steps, setSteps] = useState([]);
 const [currentStep, setCurrentStep] = useState(0);

 // Quick sort function
 const quickSort = (arr, start, stop) => {
   const steps = [];
   if(start < stop) {
     const pivotIndex = partition(arr, start, stop);
     steps.push({arr: [...arr], pivotIndex});
     steps.push(...quickSort(arr, start, pivotIndex-1));
     steps.push(...quickSort(arr, pivotIndex + 1, stop));
   }
   return steps;
 }

 // Partition function
 const partition = (arr, start, stop) => {
   const pivot = start;
   let i = start + 1;
   for(let j = start + 1; j <= stop; j++) {
     if(arr[j] <= arr[pivot]) {
       [arr[i], arr[j]] = [arr[j], arr[i]];
       i++;
     }
   }
   [arr[pivot], arr[i - 1]] = [arr[i - 1], arr[pivot]];
   return i - 1;
 }

 // Handle input field changes
 const handleInputChange = (index, event) => {
   let data = [...array];
   data[index] = event.target.value;
   setArray(data);
 }

 // Handle button click event
 // Handle button click event
const handleButtonClick = () => {
    let newArray = Array.from({length: 15}, () => Math.floor(Math.random() * 100));
    setRandomArray(newArray);
    const steps = quickSort(newArray, 0, newArray.length - 1);
    if (steps.length > 0) {
      setSteps(steps);
      setCurrentStep(0);
    }
   }


 // Render input fields, button and visualization
 return (
   <div>
     {array.map((value, index) => (
       <input
         key={index}
         value={value}
         onChange={(event) => handleInputChange(index, event)}
       />
     ))}
     <button onClick={handleButtonClick}>Generate Random Array</button>
     <button onClick={() => setCurrentStep(currentStep + 1)}>Next Step</button>
     <button onClick={handleButtonClick}>Reset</button>
     <Visualization step={steps[currentStep]} />
   </div>
 );
}

const Visualization = ({step}) => {
    if (!step) {
      return null;
    }
    return (
      <div>
        {step.arr.map((value, index) => (
          <div
            key={index}
            style={{
              height: `${value}%`,
              width: '10px',
              backgroundColor: index === step.pivotIndex ? 'red' : 'blue',
            }}
          />
        ))}
      </div>
    );
   }


export default App;
