// Quicksort.js
import React, { useState, useEffect } from "react";
import "./Quicksort.css";

const Quicksort = () => {
  const [arr, setArr] = useState([]);
  const [inputArr, setInputArr] = useState("");
  const [isSorting, setIsSorting] = useState(false);
  const [steps, setSteps] = useState([]);
  const [pivotPosition, setPivotPosition] = useState(null);
  const [currentStep, setCurrentStep] = useState(0);
  const [correctAnswer, setCorrectAnswer] = useState("");
  const [comparingNumbers, setComparingNumbers] = useState([]);


  const handleInputChange = (e) => {
    setInputArr(e.target.value);
  };

  const handleGenerateRandomArray = () => {
    setArr(generateRandomArray(15));
    setSteps([]);
    setPivotPosition(null);
    setComparingNumbers([]);
  };

  const handleSortArray = async () => {
    setIsSorting(true);
    const arrToSort = [...arr];
    const sortingSteps = [];

    // Select the initial pivot
    const pivotIndex = arrToSort.length - 1;
    setPivotPosition(pivotIndex);
    sortingSteps.push([
      arrToSort.map((_, index) => arrToSort[index]),
      pivotIndex,
      arrToSort[pivotIndex],
    ]);
    setSteps([...sortingSteps]);

    let swaps = 0;

    while (currentStep < steps.length) {
      await quicksort(arrToSort, 0, arrToSort.length - 1, sortingSteps);
      swaps++;

      // Prompt user every three swaps
      if (swaps % 3 === 0) {
        const userResponse = prompt("Enter the next state of the array (comma-separated values):");
        const expectedState = steps[currentStep][0]
          .map(({ value }) => (value !== undefined ? value : ""))
          .join(',');

        if (userResponse === expectedState) {
          alert("Correct! The algorithm will continue sorting.");
        } else {
          setCorrectAnswer(expectedState); // Set correct answer here
          alert(`Incorrect! The correct answer is: ${expectedState}`);
          setIsSorting(false);
          return;
        }
      }
    }

    setIsSorting(false);
    setCorrectAnswer(""); // Reset correct answer after sorting is complete
  };


  const generateRandomArray = (length) => {
    const arr = [];
    for (let i = 0; i < length; i++) {
      arr.push(Math.floor(Math.random() * 100));
    }
    return arr;
  };

  const quicksort = async (arr, start, end, steps) => {
    if (start >= end) {
      return;
    }

    const pivotIndex = await partition(arr, start, end, steps);

    // Pause after every three steps
    if (currentStep % 3 === 0) {
      await new Promise((resolve) => setTimeout(resolve, 1000));
    }

    setCurrentStep(currentStep + 1);

    return pivotIndex;
  };

  const partition = async (arr, start, end, steps) => {
    const pivot = arr[end];
    let i = start - 1;

    for (let j = start; j < end; j++) {
      if (arr[j] < pivot) {
        i++;
        await swap(arr, i, j, steps);
      }
    }

    await swap(arr, i + 1, end, steps);
    setPivotPosition(i + 1);
    setComparingNumbers([arr[i + 1], arr[end]]);

    return i + 1;
  };

  const swap = async (arr, i, j, steps) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
        setArr([...arr]);

        // Highlight the numbers being compared
        steps.push([
          arr.map((_, index) => ({
            value: arr[index],
            isComparing: index === i || index === j,
          })),
          pivotPosition,
        ]);
        setSteps([...steps]);

        resolve();
      }, 450);
    });
  };

  return (
    <div className="quicksort-container">
      <div className="options">
        <input
          type="text"
          placeholder="Enter array (e.g., 1, 2, 3)"
          value={inputArr}
          onChange={handleInputChange}
        />
        <button onClick={() => setArr(inputArr.split(",").map(Number))}>
          Use Input Array
        </button>
        <button onClick={handleGenerateRandomArray}>
          Generate Random Array
        </button>
        <button onClick={handleSortArray} disabled={isSorting}>
          Sort Array
        </button>
      </div>
      <div className="array-container">
        <div className="step">
          {arr.map((num, index) => (
            <div
              key={index}
              className={`circle ${
                steps.some(
                  ([_, pivot]) => pivot === pivotPosition && index !== pivot
                )
                  ? "yellow"
                  : ""
              }`}
            >
              {num}
            </div>
          ))}
        </div>
        <div className="pivot-label">
          {pivotPosition !== null && (
            <div>
              <span className="label-text">Pivot:</span>
              <span className="pivot-number">{arr[pivotPosition]}</span>
            </div>
          )}
        </div>
        {comparingNumbers.length > 0 && (
          <div className="comparing-label">
            <span className="label-text">Comparing:</span>
            {comparingNumbers.map((num, index) => (
              <span key={index} className="comparing-number">
                {num}
              </span>
            ))}
          </div>
        )}
      </div>
      {correctAnswer && (
        <div>
          <p>Correct Answer: {correctAnswer}</p>
        </div>
      )}
    </div>
  );
};

export default Quicksort;
