import React, { useState } from 'react';
import './Quicksort.css';

function Quicksort() {
  const [arr, setArr] = useState([]);
  const [inputArr, setInputArr] = useState('');
  const [isSorting, setIsSorting] = useState(false);
  const [steps, setSteps] = useState([]);
  const [initialPivotIndex, setInitialPivotIndex] = useState(null);

  const handleInputChange = (e) => {
    setInputArr(e.target.value);
  };

  const handleGenerateRandomArray = () => {
    const randomArr = generateRandomArray(10);
    setArr(randomArr);
    setSteps([]);
    setInitialPivotIndex(null);
  };

  const handleSortArray = async () => {
    setIsSorting(true);
    const arrToSort = [...arr];
    const sortingSteps = [];

    // Select and highlight the initial pivot
    const pivotIndex = arrToSort.length - 1;
    sortingSteps.push([pivotIndex, pivotIndex, true]);
    setInitialPivotIndex(pivotIndex);
    setSteps([...sortingSteps]);

    await quicksort(arrToSort, 0, arrToSort.length - 1, sortingSteps);
    setIsSorting(false);
  };

  const generateRandomArray = (length) => {
    const arr = [];
    for (let i = 0; i < length; i++) {
      arr.push(Math.floor(Math.random() * 100));
    }
    return arr;
  };

  const quicksort = async (arr, start, end, steps) => {
    if (start >= end) {
      return;
    }

    const pivotIndex = await partition(arr, start, end, steps);

    await Promise.all([
      quicksort(arr, start, pivotIndex - 1, steps),
      quicksort(arr, pivotIndex + 1, end, steps),
    ]);
  };

  const partition = async (arr, start, end, steps) => {
    const pivot = arr[end];
    let i = start - 1;

    for (let j = start; j < end; j++) {
      if (arr[j] < pivot) {
        i++;
        await swap(arr, i, j, steps);
      }
    }

    await swap(arr, i + 1, end, steps);

    return i + 1;
  };

  const swap = async (arr, i, j, steps) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
        setArr([...arr]);

        // Highlight the initially selected pivot for each iteration
        const pivotIndex = initialPivotIndex;
        if (pivotIndex !== null) {
          steps.push([pivotIndex, pivotIndex, true]);
        }

        resolve();
      }, 300); // Adjust the delay as needed
    });
  };

  return (
    <div className="quicksort-container">
      <div className="options">
        <input
          type="text"
          placeholder="Enter array (e.g., 1, 2, 3)"
          value={inputArr}
          onChange={handleInputChange}
        />
        <button onClick={() => setArr(inputArr.split(',').map(Number))}>
          Use Input Array
        </button>
        <button onClick={handleGenerateRandomArray}>Generate Random Array</button>
        <button onClick={handleSortArray} disabled={isSorting}>
          Sort Array
        </button>
      </div>
      <div className="array-container">
        {arr.map((num, index) => (
          <div
            key={index}
            className={`circle ${
              steps.some(([i, j, isPivot]) => (i === index || j === index) && isPivot)
                ? 'pivot-highlight'
                : ''
            }`}
          >
            {num}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Quicksort;
